#include <stdio.h>
#include <stdlib.h>

int main() {
    int a, b, sum, digits;

    while (scanf("%d %d", &a, &b) == 2) {
        sum = a + b;
        digits = 0;

        if (sum == 0) {
            printf("1\n");
        } else {
            while (sum != 0) {
                sum /= 10;
                digits++;
            }

            printf("%d\n", digits);
        }
    }

    return 0;
}
