#include <stdio.h>
#include <stdlib.h>

#define MAX_H 30
#define MAX_W 30

typedef struct {
    int h, w;
    int board[MAX_H][MAX_W];
    int costs[4];
} Board;

int min(int a, int b) {
    return a < b ? a : b;
}

int dfs(Board *board, int x, int y, int cost) {
    if (x == board->w - 1 && y == board->h - 1) {
        return cost;
    }

    if (x < 0 || x >= board->w || y < 0 || y >= board->h) {
        return -1;
    }

    int next_x, next_y;
    int next_cost;
    int min_cost = -1;

    switch (board->board[y][x]) {
        case 0:
            next_x = x;
            next_y = y + 1;
            next_cost = cost + board->costs[0];
            break;
        case 1:
            next_x = x + 1;
            next_y = y;
            next_cost = cost + board->costs[1];
            break;
        case 2:
            next_x = x;
            next_y = y - 1;
            next_cost = cost + board->costs[2];
            break;
        case 3:
            next_x = x - 1;
            next_y = y;
            next_cost = cost + board->costs[3];
            break;
        case 4:
            return -1;
        default:
            return -1;
    }

    int temp_cost = dfs(board, next_x, next_y, next_cost);
    if (temp_cost != -1) {
        min_cost = min(min_cost, temp_cost);
    }

    return min_cost;
}

int main() {
    int h, w;
    Board board;
    while (scanf("%d %d", &h, &w) == 2) {
        if (h == 0 && w == 0) {
            break;
        }

        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                scanf("%d", &board.board[i][j]);
            }
        }

        for (int i = 0; i < 4; i++) {
            scanf("%d", &board.costs[i]);
        }

        int min_cost = dfs(&board, 0, 0, 0);
        if (min_cost == -1) {
            printf("-1\n");
        } else {
            printf("%d\n", min_cost);
        }
    }

    return 0;
}
