#include <iostream>
#include <vector>
#include <queue>
#include <limits>

using namespace std;

const int INF = numeric_limits<int>::max();

struct Node {
    int x, y, cost, direction;
};

int main() {
    int h, w;
    while (cin >> h >> w) {
        if (h == 0 && w == 0) break;

        vector<vector<int>> board(h, vector<int>(w));
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> board[i][j];
            }
        }

        vector<vector<int>> costs(4);
        for (int i = 0; i < 4; i++) {
            cin >> costs[i];
        }

        vector<vector<int>> directions = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        vector<vector<int>> visited(h, vector<int>(w, INF));
        queue<Node> q;
        q.push({0, 0, 0, 0});
        visited[0][0] = 0;

        while (!q.empty()) {
            Node node = q.front();
            q.pop();

            if (node.x == h - 1 && node.y == w - 1) {
                cout << node.cost << endl;
                break;
            }

            int command = board[node.x][node.y];
            int newCost = node.cost + costs[command];
            int newDirection = (node.direction + command) % 4;

            for (int i = 0; i < 4; i++) {
                int newX = node.x + directions[i][0];
                int newY = node.y + directions[i][1];

                if (newX >= 0 && newX < h && newY >= 0 && newY < w && visited[newX][newY] > newCost) {
                    visited[newX][newY] = newCost;
                    q.push({newX, newY, newCost, newDirection});
                }
            }
        }
    }

    return 0;
}
