import java.util.*;

public class Main {
    static class Robot {
        int x, y, cost;
        int[][] dir = {{0, 1}, {1, 0}, {0, -1}, {-1, 0}};
        int[] commands = {0, 1, 2, 3};
        int[] costs = {0, 1, 2, 3};
        int[][] dp;

        public Robot(int x, int y, int[][] commands, int[] costs) {
            this.x = x;
            this.y = y;
            this.commands = commands;
            this.costs = costs;
            this.dp = new int[31][31];
            for (int i = 0; i < 31; i++) {
                Arrays.fill(dp[i], Integer.MAX_VALUE);
            }
            dp[x][y] = 0;
        }

        public int move(int dx, int dy) {
            int nx = x + dx;
            int ny = y + dy;
            if (nx < 0 || nx >= dp.length || ny < 0 || ny >= dp[0].length) {
                return Integer.MAX_VALUE;
            }
            return dp[nx][ny];
        }

        public int dfs(int x, int y) {
            if (dp[x][y] != Integer.MAX_VALUE) {
                return dp[x][y];
            }
            int min = Integer.MAX_VALUE;
            for (int i = 0; i < 4; i++) {
                int nx = x + dir[i][0];
                int ny = y + dir[i][1];
                int cost = costs[commands[nx][ny]];
                int newCost = cost + move(dir[i][0], dir[i][1]);
                min = Math.min(min, newCost);
            }
            dp[x][y] = min;
            return min;
        }
    }

    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int h = scanner.nextInt();
            int w = scanner.nextInt();
            if (h == 0 && w == 0) {
                break;
            }
            int[][] commands = new int[h][w];
            for (int i = 0; i < h; i++) {
                for (int j = 0; j < w; j++) {
                    commands[i][j] = scanner.nextInt();
                }
            }
            int[] costs = new int[4];
            for (int i = 0; i < 4; i++) {
                costs[i] = scanner.nextInt();
            }
            Robot robot = new Robot(0, 0, commands, costs);
            int minCost = robot.dfs(h - 1, w - 1);
            System.out.println(minCost);
        }
    }
}
