#include <stdio.h>
#include <math.h>

#define MAXN 55

typedef struct {
    int minx, miny, maxx, maxy, h;
} Block;

typedef struct {
    int x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

int intersect(Point p1, Point p2, Block b) {
    if (p1.x >= b.minx && p1.x <= b.maxx && p1.y >= b.miny && p1.y <= b.maxy) return 1;
    if (p2.x >= b.minx && p2.x <= b.maxx && p2.y >= b.miny && p2.y <= b.maxy) return 1;
    if (p1.x <= b.minx && p2.x >= b.minx && p1.y <= b.miny && p2.y >= b.miny) return 1;
    if (p1.x <= b.maxx && p2.x >= b.maxx && p1.y <= b.maxy && p2.y >= b.maxy) return 1;
    if (p1.y <= b.miny && p2.y >= b.miny && p1.x <= b.minx && p2.x >= b.minx) return 1;
    if (p1.y <= b.maxy && p2.y >= b.maxy && p1.x <= b.maxx && p2.x >= b.maxx) return 1;
    return 0;
}

int main() {
    int n, i, j;
    double r, r1, r2, r3, r4;
    Point s, e;
    Block b[MAXN];

    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        scanf("%d %d %d %d", &s.x, &s.y, &e.x, &e.y);
        for (i = 0; i < n; i++) {
            scanf("%d %d %d %d %d", &b[i].minx, &b[i].miny, &b[i].maxx, &b[i].maxy, &b[i].h);
        }
        r = 0;
        for (i = 0; i < n; i++) {
            r1 = distance(s, e);
            r2 = distance(s, (Point){b[i].minx, b[i].miny});
            r3 = distance(s, (Point){b[i].maxx, b[i].miny});
            r4 = distance(s, (Point){b[i].minx, b[i].maxy});
            if (intersect(s, (Point){b[i].maxx, b[i].maxy}, b[i]) || intersect(s, (Point){b[i].minx, b[i].maxy}, b[i]) || intersect(s, (Point){b[i].maxx, b[i].miny}, b[i]) || intersect(s, (Point){b[i].minx, b[i].miny}, b[i])) {
                r = 0;
                break;
            }
            r1 = min(r1, r2);
            r1 = min(r1, r3);
            r1 = min(r1, r4);
            r = max(r, r1);
        }
        printf("%.6lf\n", r);
    }
    return 0;
}
