import math

def check_collision(x, y, r, blocks):
    for block in blocks:
        minx, miny, maxx, maxy, h = block
        if (minx <= x <= maxx and miny <= y <= maxy and
            math.sqrt((x - minx)**2 + (y - miny)**2) <= r + h):
            return True
    return False

def find_radius(sx, sy, ex, ey, blocks):
    if (sx, sy) == (ex, ey):
        return 0
    for r in range(1001):
        x, y = sx, sy
        while x < ex:
            if check_collision(x, y, r, blocks):
                break
            x += 1
            if x == ex and y == ey:
                return r
        if x == ex and y == ey:
            return r
    return 0

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        sx, sy, ex, ey = map(int, input().split())
        blocks = []
        for _ in range(n):
            minx, miny, maxx, maxy, h = map(int, input().split())
            blocks.append((minx, miny, maxx, maxy, h))
        print(find_radius(sx, sy, ex, ey, blocks))

if __name__ == "__main__":
    main()
