#include <stdio.h>
#include <stdlib.h>

#define MAX_W 12
#define MAX_H 12

int w, h;
int grid[MAX_H][MAX_W];

int dfs(int x, int y, int color) {
    if (x < 0 || x >= w || y < 0 || y >= h || grid[y][x] != 1) {
        return 0;
    }
    grid[y][x] = color;
    return dfs(x - 1, y, color) + dfs(x + 1, y, color) + dfs(x, y - 1, color) + dfs(x, y + 1, color);
}

int is_bipartite() {
    int color = 2;
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] == 1) {
                if (dfs(j, i, color) % 2 == 0) {
                    return 1;
                }
                color++;
            }
        }
    }
    return 0;
}

int main() {
    int t;
    scanf("%d", &t);
    while (t--) {
        scanf("%d %d", &w, &h);
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                scanf("%1d", &grid[i][j]);
            }
        }
        if (is_bipartite()) {
            printf("YES\n");
        } else {
            printf("NO\n");
        }
    }
    return 0;
}
