#include <iostream>
#include <vector>
#include <queue>
#include <set>
#include <string>

using namespace std;

const int MAXN = 12;

int n, m;
vector<vector<int>> grid;
vector<vector<int>> visited;
vector<vector<int>> parent;
vector<vector<int>> depth;
vector<vector<int>> stack;
vector<int> order;
vector<int> comp;
vector<int> compSize;
vector<int> compDepth;
int compCount;
int compSizeSum;
int compDepthSum;
int maxDepth;
int maxCompSize;
int maxCompDepth;

void dfs(int x, int y) {
    if (x < 0 || x >= n || y < 0 || y >= m || grid[x][y] == 0 || visited[x][y]) {
        return;
    }
    visited[x][y] = 1;
    stack.push_back(x);
    order.push_back(y);
    parent[x][y] = -1;
    depth[x][y] = 0;
    for (int i = -1; i <= 1; i++) {
        for (int j = -1; j <= 1; j++) {
            if (i == 0 && j == 0) {
                continue;
            }
            int nx = x + i;
            int ny = y + j;
            if (nx < 0 || nx >= n || ny < 0 || ny >= m || grid[nx][ny] == 0) {
                continue;
            }
            if (visited[nx][ny]) {
                continue;
            }
            parent[nx][ny] = x;
            depth[nx][ny] = depth[x][y] + 1;
            dfs(nx, ny);
        }
    }
    stack.pop_back();
    order.pop_back();
}

void findComponents() {
    compCount = 0;
    compSizeSum = 0;
    compDepthSum = 0;
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < m; j++) {
            if (grid[i][j] == 1 && !visited[i][j]) {
                compCount++;
                compSizeSum = 0;
                compDepthSum = 0;
                stack.clear();
                order.clear();
                dfs(i, j);
                compSizeSum = stack.size();
                compDepthSum = maxDepth;
                comp[compCount] = compSizeSum;
                compSize[compCount] = compSizeSum;
                compDepth[compCount] = compDepthSum;
            }
        }
    }
}

void findMaxComponent() {
    maxCompSize = 0;
    maxCompDepth = 0;
    for (int i = 1; i <= compCount; i++) {
        if (compSize[i] > maxCompSize) {
            maxCompSize = compSize[i];
            maxCompDepth = compDepth[i];
        } else if (compSize[i] == maxCompSize) {
            if (compDepth[i] > maxCompDepth) {
                maxCompDepth = compDepth[i];
            }
        }
    }
}

int main() {
    while (true) {
        cin >> n >> m;
        if (n == 0 && m == 0) {
            break;
        }
        grid.resize(n, vector<int>(m));
        visited.resize(n, vector<int>(m));
        parent.resize(n, vector<int>(m));
        depth.resize(n, vector<int>(m));
        stack.resize(n * m);
        order.resize(n * m);
        comp.resize(n * m + 1);
        compSize.resize(n * m + 1);
        compDepth.resize(n * m + 1);
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < m; j++) {
                cin >> grid[i][j];
            }
        }
        findComponents();
        findMaxComponent();
        if (maxCompSize == 0) {
            cout << "NO" << endl;
        } else if (maxCompSize == compSizeSum && maxCompDepth == compDepthSum) {
            cout << "YES" << endl;
        } else {
            cout << "NO" << endl;
        }
    }
    return 0;
}
