#include <iostream>
#include <string>

int main() {
    std::string text;
    std::getline(std::cin, text);

    for (char& c : text) {
        if (c >= 'a' && c <= 'z') {
            c -= 32; // ASCII value of 'a' is 97 and 'A' is 65, so subtract 32 to get the corresponding uppercase letter
        }
    }

    std::cout << text << std::endl;

    return 0;
}
