#include <stdio.h>
#include <stdlib.h>

#define MAX_SIZE 50

int visited[MAX_SIZE][MAX_SIZE];
int grid[MAX_SIZE][MAX_SIZE];

void dfs(int i, int j) {
    if (i < 0 || j < 0 || i >= grid[0][0] || j >= grid[0][1] || grid[i][j] == 0 || visited[i][j] == 1) {
        return;
    }
    visited[i][j] = 1;
    dfs(i - 1, j);
    dfs(i + 1, j);
    dfs(i, j - 1);
    dfs(i, j + 1);
    dfs(i - 1, j - 1);
    dfs(i - 1, j + 1);
    dfs(i + 1, j - 1);
    dfs(i + 1, j + 1);
}

int main() {
    int w, h, island_count = 0;
    scanf("%d %d", &w, &h);
    while (w != 0 && h != 0) {
        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                scanf("%d", &grid[i][j]);
            }
        }
        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                if (grid[i][j] == 1 && visited[i][j] == 0) {
                    dfs(i, j);
                    island_count++;
                }
            }
        }
        printf("%d\n", island_count);
        island_count = 0;
        for (int i = 0; i < w; i++) {
            for (int j = 0; j < h; j++) {
                visited[i][j] = 0;
            }
        }
        scanf("%d %d", &w, &h);
    }
    return 0;
}
