#include <stdio.h>
#include <string.h>
#include <stdlib.h>

#define MAXN 13
#define MAXM 9
#define MAXA 26

int n, m, a;
char s[MAXN][MAXM + 1];
int d[MAXA];
int cnt[MAXA];

void dfs(int x) {
    if (x == a) {
        int i, j, k, c;
        int sum = 0;
        for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
                c = 0;
                for (k = 0; k < a; k++) {
                    if (s[i][j] == k + 'A') {
                        c = d[k];
                    }
                }
                sum += c * (10 << (m - 1 - j));
            }
        }
        if (sum == 0 || sum == (10 << m) - 1) {
            int i;
            for (i = 0; i < a; i++) {
                if (d[i] != 0) {
                    break;
                }
            }
            if (i == a) {
                cnt[0]++;
            }
        }
        return;
    }
    for (int i = 0; i < 10; i++) {
        d[x] = i;
        dfs(x + 1);
    }
}

int main() {
    int i, j, k, c;
    int t;
    while (scanf("%d", &t) == 1 && t != 0) {
        scanf("%d", &n);
        for (i = 0; i < n; i++) {
            scanf("%s", s[i]);
            m = strlen(s[i]);
        }
        a = 0;
        for (i = 0; i < n; i++) {
            for (j = 0; j < m; j++) {
                c = 0;
                for (k = 0; k < a; k++) {
                    if (s[i][j] == k + 'A') {
                        c = 1;
                        break;
                    }
                }
                if (c == 0) {
                    a++;
                }
            }
        }
        cnt[0] = 0;
        dfs(0);
        printf("%d\n", cnt[0]);
    }
    return 0;
}
