#include <iostream>
#include <vector>
#include <string>
#include <unordered_map>
#include <algorithm>

using namespace std;

int countAssignments(const vector<string>& nums) {
    unordered_map<char, int> digitMap;
    int numDigits = 0;

    for (const string& num : nums) {
        for (char c : num) {
            if (digitMap.find(c) == digitMap.end()) {
                digitMap[c] = numDigits++;
            }
        }
    }

    int numAssignments = 1;
    for (int i = 0; i < numDigits; ++i) {
        numAssignments *= (i + 1);
    }

    for (const string& num : nums) {
        int numUniqueDigits = 0;
        for (char c : num) {
            if (digitMap[c] == numUniqueDigits) {
                return 0;
            }
            numUniqueDigits++;
        }
    }

    return numAssignments;
}

int main() {
    int numDatasets;
    cin >> numDatasets;

    while (numDatasets--) {
        int numIntegers;
        cin >> numIntegers;

        vector<string> nums(numIntegers);
        for (string& num : nums) {
            cin >> num;
        }

        int numAssignments = countAssignments(nums);
        cout << numAssignments << endl;
    }

    return 0;
}
