import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null && !line.equals("0")) {
            int n = Integer.parseInt(line);
            String[] strings = new String[n];
            for (int i = 0; i < n; i++) {
                strings[i] = br.readLine();
            }
            System.out.println(countAssignments(strings));
        }
    }

    private static long countAssignments(String[] strings) {
        Map<Character, Integer> map = new HashMap<>();
        int n = strings.length;
        for (int i = 0; i < n - 1; i++) {
            for (char c : strings[i].toCharArray()) {
                map.put(c, map.getOrDefault(c, 0) + 1);
            }
        }
        for (char c : strings[n - 1].toCharArray()) {
            map.put(c, map.getOrDefault(c, 0) + 1);
        }
        long count = 1;
        for (int value : map.values()) {
            count *= factorial(value);
        }
        for (int i = 0; i < n - 1; i++) {
            if (strings[i].length() == 1 && strings[i].charAt(0) == '0') {
                return 0;
            }
        }
        return count;
    }

    private static long factorial(int n) {
        long result = 1;
        for (int i = 2; i <= n; i++) {
            result *= i;
        }
        return result;
    }
}
