#include <stdio.h>
#include <limits.h>
#include <math.h>

#define INF 1e9

typedef struct {
    int x, y, d, c;
} Edge;

int n, m, s, g;
Edge edges[1000];
double dist[1000][1000];

void dijkstra(int start) {
    for (int i = 0; i < n; i++) {
        for (int j = 0; j < n; j++) {
            dist[i][j] = INF;
        }
    }
    dist[start][start] = 0;
    for (int i = 0; i < n; i++) {
        int u = -1;
        for (int j = 0; j < n; j++) {
            if (u == -1 || dist[start][u] > dist[start][j]) {
                u = j;
            }
        }
        for (int j = 0; j < n; j++) {
            if (dist[start][u] + dist[u][j] < dist[start][j]) {
                dist[start][j] = dist[start][u] + dist[u][j];
            }
        }
    }
}

int main() {
    while (scanf("%d %d", &n, &m) == 2) {
        if (n == 0 && m == 0) break;
        scanf("%d %d", &s, &g);
        s--; g--;
        for (int i = 0; i < m; i++) {
            scanf("%d %d %d %d", &edges[i].x, &edges[i].y, &edges[i].d, &edges[i].c);
            edges[i].x--; edges[i].y--;
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                dist[i][j] = INF;
            }
        }
        for (int i = 0; i < m; i++) {
            int x = edges[i].x, y = edges[i].y, d = edges[i].d, c = edges[i].c;
            for (int v = c - 1; v <= c + 1; v++) {
                if (v >= 1 && v <= c) {
                    dist[x][y] = min(dist[x][y], (double)d / v);
                }
            }
        }
        for (int i = 0; i < n; i++) {
            for (int j = 0; j < n; j++) {
                for (int k = 0; k < n; k++) {
                    dist[i][j] = min(dist[i][j], dist[i][k] + dist[k][j]);
                }
            }
        }
        if (dist[s][g] == INF) {
            printf("unreachable\n");
        } else {
            printf("%.5f\n", dist[s][g]);
        }
    }
    return 0;
}
