import sys
import heapq

def dijkstra(graph, start, goal):
    queue = []
    heapq.heappush(queue, (0, start))
    distances = {node: float('inf') for node in graph}
    distances[start] = 0
    while queue:
        current_distance, current_node = heapq.heappop(queue)
        if current_distance > distances[current_node]:
            continue
        for neighbor, weight in graph[current_node].items():
            distance = current_distance + weight
            if distance < distances[neighbor]:
                distances[neighbor] = distance
                heapq.heappush(queue, (distance, neighbor))
    return distances[goal]

def solve(graph, start, goal):
    distances = {}
    for speed in range(1, 31):
        distances[speed] = dijkstra(graph, start, goal, speed)
    if min(distances.values()) == float('inf'):
        return 'unreachable'
    return str(min(distances.values()))

def main():
    input_lines = sys.stdin.readlines()
    current_line = 0
    while True:
        n, m = map(int, input_lines[current_line].split())
        current_line += 1
        start, goal = map(int, input_lines[current_line].split())
        current_line += 1
        graph = {}
        for _ in range(n):
            graph[_] = {}
        for _ in range(m):
            x, y, d, c = map(int, input_lines[current_line].split())
            current_line += 1
            graph[x][y] = d / c
            graph[y][x] = d / c
        print(solve(graph, start, goal))

if __name__ == "__main__":
    main()
