#include <iostream>
#include <vector>
#include <algorithm>
#include <numeric>

using namespace std;

int gcd(int a, int b) {
    if (b == 0)
        return a;
    return gcd(b, a % b);
}

int main() {
    int m, n;
    while (cin >> m >> n) {
        if (m == 0 && n == 0)
            break;

        vector<int> blue(m), red(n);
        for (int i = 0; i < m; i++)
            cin >> blue[i];
        for (int i = 0; i < n; i++)
            cin >> red[i];

        vector<int> blueFactors(m), redFactors(n);
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                blueFactors[i] = gcd(blueFactors[i], red[j]);
                redFactors[j] = gcd(redFactors[j], blue[i]);
            }
        }

        int count = 0;
        for (int i = 0; i < m; i++) {
            for (int j = 0; j < n; j++) {
                if (blueFactors[i] == redFactors[j] && blueFactors[i] > 1)
                    count++;
            }
        }

        cout << count / 2 << endl;
    }

    return 0;
}
