import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String line;
        while ((line = br.readLine()) != null) {
            if (line.equals("0 0")) break;
            String[] parts = line.split(" ");
            int m = Integer.parseInt(parts[0]);
            int n = Integer.parseInt(parts[1]);
            List<Integer> blue = new ArrayList<>();
            List<Integer> red = new ArrayList<>();
            for (int i = 0; i < m; i++) {
                line = br.readLine();
                parts = line.split(" ");
                for (String part : parts) {
                    blue.add(Integer.parseInt(part));
                }
            }
            for (int i = 0; i < n; i++) {
                line = br.readLine();
                parts = line.split(" ");
                for (String part : parts) {
                    red.add(Integer.parseInt(part));
                }
            }
            Map<Integer, Integer> blueMap = new HashMap<>();
            for (int num : blue) {
                blueMap.put(num, blueMap.getOrDefault(num, 0) + 1);
            }
            Map<Integer, Integer> redMap = new HashMap<>();
            for (int num : red) {
                redMap.put(num, redMap.getOrDefault(num, 0) + 1);
            }
            int count = 0;
            for (int num : blue) {
                for (int other : red) {
                    if (gcd(num, other) > 1) {
                        count += Math.min(blueMap.get(num), redMap.get(other));
                        blueMap.put(num, blueMap.get(num) - redMap.get(other));
                        redMap.put(other, redMap.get(other) - blueMap.get(num));
                    }
                }
            }
            System.out.println(count);
        }
    }

    public static int gcd(int a, int b) {
        if (b == 0) return a;
        return gcd(b, a % b);
    }
}
