#include <stdio.h>
#include <math.h>

#define EPS 1e-6

typedef struct {
    double x, y;
} Point;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double crossProduct(Point p1, Point p2, Point p3) {
    return (p2.x - p1.x) * (p3.y - p1.y) - (p2.y - p1.y) * (p3.x - p1.x);
}

int main() {
    int m, n;
    Point points[200];

    while (scanf("%d %d", &m, &n) == 2) {
        if (m == 0 && n == 0) break;

        for (int i = 0; i < m + n; i++) {
            scanf("%lf %lf", &points[i].x, &points[i].y);
        }

        double totalLength = 0;
        for (int i = 0; i < m - 1; i++) {
            totalLength += distance(points[i], points[i + 1]);
        }

        for (int i = 0; i < m - 1; i++) {
            for (int j = m; j < m + n; j++) {
                if (crossProduct(points[i], points[i + 1], points[j]) < 0) {
                    totalLength -= distance(points[i], points[i + 1]);
                    totalLength += distance(points[i], points[j]);
                    totalLength += distance(points[i + 1], points[j]);
                }
            }
        }

        printf("%.6f\n", totalLength);
    }

    return 0;
}
