import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int m = scanner.nextInt();
            int n = scanner.nextInt();
            int[][] points = new int[m + n][2];
            for (int i = 0; i < m + n; i++) {
                points[i][0] = scanner.nextInt();
                points[i][1] = scanner.nextInt();
            }
            double length = calculateLength(points);
            System.out.printf("%.6f%n", length);
            if (m == 0 && n == 0) {
                break;
            }
        }
    }

    public static double calculateLength(int[][] points) {
        int n = points.length;
        double[] x = new double[n];
        double[] y = new double[n];
        for (int i = 0; i < n; i++) {
            x[i] = points[i][0];
            y[i] = points[i][1];
        }
        double[] dx = new double[n];
        double[] dy = new double[n];
        for (int i = 1; i < n; i++) {
            dx[i] = x[i] - x[i - 1];
            dy[i] = y[i] - y[i - 1];
        }
        double[] lengths = new double[n - 1];
        for (int i = 1; i < n - 1; i++) {
            lengths[i - 1] = Math.sqrt(dx[i] * dx[i] + dy[i] * dy[i]);
        }
        double[] angles = new double[n - 1];
        for (int i = 1; i < n - 1; i++) {
            angles[i - 1] = Math.atan2(dy[i], dx[i]);
        }
        double[] sortedAngles = angles.clone();
        Arrays.sort(sortedAngles);
        int[] sortedIndices = new int[n - 1];
        for (int i = 0; i < n - 1; i++) {
            sortedIndices[i] = Arrays.binarySearch(angles, sortedAngles[i]);
        }
        double[] sortedLengths = lengths.clone();
        Arrays.sort(sortedLengths, (a, b) -> {
            int ia = Arrays.binarySearch(angles, a);
            int ib = Arrays.binarySearch(angles, b);
            return Integer.compare(ia, ib);
        });
        double[] dp = new double[n];
        for (int i = 0; i < n; i++) {
            dp[i] = Double.POSITIVE_INFINITY;
        }
        dp[0] = 0;
        for (int i = 1; i < n; i++) {
            for (int j = 0; j < i; j++) {
                double angle = angles[sortedIndices[j]];
                double length = sortedLengths[j];
                if (Math.abs(angle - angles[i]) < 1e-6) {
                    dp[i] = Math.min(dp[i], dp[j] + length);
                }
            }
        }
        return dp[n - 1];
    }
}
