#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int x, y;
} Square;

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Square squares[n];
        int width = 1, height = 1;
        for (int i = 0; i < n - 1; i++) {
            int x, y;
            scanf("%d %d", &x, &y);
            if (x == 0) {
                if (y == 0) width++;
                else if (y == 1) height++;
                else if (y == 2) width++;
                else if (y == 3) height++;
            } else {
                if (squares[x - 1].y == y) {
                    if (y == 0) width++;
                    else if (y == 1) height++;
                    else if (y == 2) width++;
                    else if (y == 3) height++;
                } else {
                    if (y == 0) {
                        if (squares[x - 1].x == 0) width++;
                        else if (squares[x - 1].x == width - 1) width++;
                        else {
                            width = width + 2;
                            height++;
                        }
                    } else if (y == 1) {
                        if (squares[x - 1].y == 0) height++;
                        else if (squares[x - 1].y == height - 1) height++;
                        else {
                            height = height + 2;
                            width++;
                        }
                    } else if (y == 2) {
                        if (squares[x - 1].x == width - 1) width++;
                        else if (squares[x - 1].x == 0) width++;
                        else {
                            width = width + 2;
                            height++;
                        }
                    } else if (y == 3) {
                        if (squares[x - 1].y == height - 1) height++;
                        else if (squares[x - 1].y == 0) height++;
                        else {
                            height = height + 2;
                            width++;
                        }
                    }
                }
            }
            squares[i].x = x;
            squares[i].y = y;
        }
        printf("%d %d\n", width, height);
    }
    return 0;
}
