#include <iostream>
#include <vector>
#include <string>
#include <sstream>

using namespace std;

int main() {
    int n;
    while (cin >> n && n != 0) {
        vector<int> x(n - 1), y(n - 1);
        for (int i = 0; i < n - 1; i++) {
            int a, b;
            cin >> a >> b;
            x[i] = a;
            y[i] = b;
        }

        int maxX = 0, maxY = 0;
        for (int i = 0; i < n - 1; i++) {
            if (x[i] == 0) {
                maxX++;
            } else {
                maxX = max(maxX, x[i] + 1);
            }
            if (y[i] == 0) {
                maxY++;
            } else {
                maxY = max(maxY, y[i] + 1);
            }
        }

        cout << maxX << " " << maxY << endl;
    }

    return 0;
}
