import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) {
                break;
            }
            int[] x = new int[n];
            int[] y = new int[n];
            x[0] = 0;
            y[0] = 0;
            for (int i = 1; i < n; i++) {
                int ni = scanner.nextInt();
                int di = scanner.nextInt();
                if (di == 0) {
                    x[i] = x[ni] - 1;
                } else if (di == 1) {
                    y[i] = y[ni] + 1;
                } else if (di == 2) {
                    x[i] = x[ni] + 1;
                } else {
                    y[i] = y[ni] - 1;
                }
            }
            int maxX = 0;
            int maxY = 0;
            for (int i = 0; i < n; i++) {
                maxX = Math.max(maxX, x[i]);
                maxY = Math.max(maxY, y[i]);
            }
            System.out.println((maxX + 1) + " " + (maxY + 1));
        }
    }
}
