#include <stdio.h>
#include <stdlib.h>

#define MAX_WIDTH 30
#define MAX_HEIGHT 30

int maze[MAX_HEIGHT][MAX_WIDTH];
int distance[MAX_HEIGHT][MAX_WIDTH];

int bfs(int x, int y) {
    int queue[MAX_WIDTH * MAX_HEIGHT];
    int front = 0, rear = 0;
    queue[rear++] = y * MAX_WIDTH + x;
    distance[y][x] = 1;

    while (front < rear) {
        int index = queue[front++];
        int i = index % MAX_WIDTH;
        int j = index / MAX_WIDTH;

        if (i == MAX_WIDTH - 1 && j == MAX_HEIGHT - 1) {
            return distance[j][i];
        }

        if (i > 0 && maze[j][i - 1] == 0 && distance[j][i - 1] == 0) {
            queue[rear++] = j * MAX_WIDTH + i - 1;
            distance[j][i - 1] = distance[j][i] + 1;
        }

        if (i < MAX_WIDTH - 1 && maze[j][i + 1] == 0 && distance[j][i + 1] == 0) {
            queue[rear++] = j * MAX_WIDTH + i + 1;
            distance[j][i + 1] = distance[j][i] + 1;
        }

        if (j > 0 && maze[j - 1][i] == 0 && distance[j - 1][i] == 0) {
            queue[rear++] = (j - 1) * MAX_WIDTH + i;
            distance[j - 1][i] = distance[j][i] + 1;
        }

        if (j < MAX_HEIGHT - 1 && maze[j + 1][i] == 0 && distance[j + 1][i] == 0) {
            queue[rear++] = (j + 1) * MAX_WIDTH + i;
            distance[j + 1][i] = distance[j][i] + 1;
        }
    }

    return 0;
}

int main() {
    int w, h;
    while (scanf("%d %d", &w, &h) == 2) {
        if (w == 0 && h == 0) {
            break;
        }

        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                scanf("%d", &maze[i][j]);
            }
        }

        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                distance[i][j] = 0;
            }
        }

        int result = bfs(0, 0);
        printf("%d\n", result);
    }

    return 0;
}
