#include <iostream>
#include <queue>
#include <vector>
#include <string>

using namespace std;

const int dx[] = {0, 0, 1, -1};
const int dy[] = {1, -1, 0, 0};

int bfs(vector<vector<int>>& maze, int w, int h) {
    queue<pair<int, int>> q;
    vector<vector<bool>> visited(w, vector<bool>(h, false));
    q.push({0, 0});
    visited[0][0] = true;

    int steps = 0;
    while (!q.empty()) {
        int size = q.size();
        for (int i = 0; i < size; i++) {
            int x = q.front().first;
            int y = q.front().second;
            q.pop();

            if (x == w - 1 && y == h - 1) return steps;

            for (int j = 0; j < 4; j++) {
                int nx = x + dx[j];
                int ny = y + dy[j];

                if (nx >= 0 && nx < w && ny >= 0 && ny < h && !visited[nx][ny] && maze[nx][ny] == 0) {
                    q.push({nx, ny});
                    visited[nx][ny] = true;
                }
            }
        }
        steps++;
    }

    return 0;
}

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;

        vector<vector<int>> maze(w, vector<int>(h));
        for (int i = 0; i < h; i++) {
            string line;
            if (i == 0) {
                getline(cin, line);
                for (int j = 1; j < w; j++) {
                    maze[j][i] = line[j] - '0';
                }
            } else {
                getline(cin, line);
                for (int j = 0; j < w; j++) {
                    maze[j][i] = line[j] - '0';
                }
            }
        }

        cout << bfs(maze, w, h) << endl;
    }

    return 0;
}
