import java.util.Arrays;
import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int n = scanner.nextInt();
            if (n == 0) break;
            int[] tetrahedral = new int[1000001];
            int[] oddTetrahedral = new int[500001];
            Arrays.fill(tetrahedral, 0);
            Arrays.fill(oddTetrahedral, 0);
            for (int i = 1; i <= 1000000; i++) {
                tetrahedral[i] = i * (i + 1) * (i + 2) / 6;
                if (i % 2 == 1) oddTetrahedral[i] = i * (i + 1) * (i + 2) / 6;
            }
            int[] dp = new int[n + 1];
            int[] oddDp = new int[n + 1];
            Arrays.fill(dp, Integer.MAX_VALUE);
            Arrays.fill(oddDp, Integer.MAX_VALUE);
            dp[0] = 0;
            oddDp[0] = 0;
            for (int i = 1; i <= n; i++) {
                for (int j = 1; j <= 1000000; j++) {
                    if (tetrahedral[j] > i) break;
                    dp[i] = Math.min(dp[i], dp[i - tetrahedral[j]] + 1);
                    if (j % 2 == 1) oddDp[i] = Math.min(oddDp[i], oddDp[i - tetrahedral[j]] + 1);
                }
            }
            System.out.println(dp[n] + " " + oddDp[n]);
        }
    }
}
