def tetrahedral(n):
    return n * (n + 1) * (n + 2) // 6

def odd_tetrahedral(n):
    return tetrahedral(n) if n % 2 == 1 else 0

def min_tetrahedral_sum(n):
    min_sum = float('inf')
    min_odd_sum = float('inf')
    for i in range(1, n + 1):
        sum_ = 0
        odd_sum = 0
        for j in range(i, n + 1):
            sum_ += tetrahedral(j)
            odd_sum += odd_tetrahedral(j)
            if sum_ >= n:
                if sum_ - n < min_sum:
                    min_sum = sum_ - n
                if odd_sum < min_odd_sum:
                    min_odd_sum = odd_sum
                break
    return min_sum, min_odd_sum

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        print(min_tetrahedral_sum(n))

if __name__ == "__main__":
    main()
