#include <iostream>
#include <vector>
#include <string>
#include <algorithm>

using namespace std;

struct Block {
    int x, y, id;
};

bool isStable(vector<vector<string>>& grid, vector<Block>& blocks, int x, int y) {
    int left = x, right = x;
    for (auto& block : blocks) {
        if (block.x == x && block.y == y) {
            left = min(left, block.x);
            right = max(right, block.x);
        }
    }
    int sum = 0;
    for (auto& block : blocks) {
        if (block.x == x && block.y == y) {
            sum += block.id * (block.x + 1);
        }
    }
    return left < sum && sum < right;
}

bool checkStability(vector<vector<string>>& grid, vector<Block>& blocks) {
    int w = grid[0].size(), h = grid.size();
    for (int i = 0; i < h; i++) {
        for (int j = 0; j < w; j++) {
            if (grid[i][j] != '.') {
                int x = j, y = i;
                if (y > 0 && grid[y - 1][x] != '.') {
                    if (!isStable(grid, blocks, x, y - 1)) return false;
                }
                if (y < h - 1 && grid[y + 1][x] != '.') {
                    if (!isStable(grid, blocks, x, y + 1)) return false;
                }
                if (x > 0 && grid[y][x - 1] != '.') {
                    if (!isStable(grid, blocks, x - 1, y)) return false;
                }
                if (x < w - 1 && grid[y][x + 1] != '.') {
                    if (!isStable(grid, blocks, x + 1, y)) return false;
                }
            }
        }
    }
    return true;
}

int main() {
    int w, h;
    while (cin >> w >> h) {
        if (w == 0 && h == 0) break;
        vector<vector<string>> grid(h, vector<string>(w));
        vector<Block> blocks;
        for (int i = 0; i < h; i++) {
            for (int j = 0; j < w; j++) {
                cin >> grid[i][j];
                if (grid[i][j] != '.') {
                    blocks.push_back({j, i, grid[i][j] - '0'});
                }
            }
        }
        if (checkStability(grid, blocks)) {
            cout << "STABLE" << endl;
        } else {
            cout << "UNSTABLE" << endl;
        }
    }
    return 0;
}
