import java.util.*;
import java.io.*;

public class Main {
    static class Point {
        int x, y, id;

        public Point(int x, int y, int id) {
            this.x = x;
            this.y = y;
            this.id = id;
        }
    }

    static class Piece {
        int xL, xR, M;
        List<Point> points;

        public Piece(int xL, int xR, int M, List<Point> points) {
            this.xL = xL;
            this.xR = xR;
            this.M = M;
            this.points = points;
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input;
        while ((input = br.readLine().split(" ")) != null && input.length > 0 && input[0].equals("0")) {
            input = br.readLine().split(" ");
            int w = Integer.parseInt(input[0]);
            int h = Integer.parseInt(input[1]);
            Piece[] pieces = new Piece[w];
            for (int i = 0; i < w; i++) {
                pieces[i] = new Piece(0, 0, 0, new ArrayList<>());
            }
            for (int i = 0; i < h; i++) {
                char[] row = br.readLine().toCharArray();
                for (int j = 0; j < w; j++) {
                    if (row[j] != '.') {
                        pieces[j].points.add(new Point(j, i, row[j] - '0'));
                    }
                }
            }
            for (int i = 0; i < w; i++) {
                if (pieces[i].points.size() > 0) {
                    pieces[i].xL = pieces[i].points.get(0).x;
                    pieces[i].xR = pieces[i].points.get(pieces[i].points.size() - 1).x;
                    for (Point p : pieces[i].points) {
                        pieces[i].M += p.x;
                    }
                    pieces[i].M /= pieces[i].points.size();
                }
            }
            boolean stable = true;
            for (Piece piece : pieces) {
                if (piece.points.size() > 0) {
                    int xL = Integer.MAX_VALUE, xR = Integer.MIN_VALUE;
                    for (Point p : piece.points) {
                        xL = Math.min(xL, p.x);
                        xR = Math.max(xR, p.x);
                    }
                    if (xL < piece.M && piece.M < xR) {
                        continue;
                    } else {
                        stable = false;
                        break;
                    }
                }
            }
            System.out.println(stable ? "STABLE" : "UNSTABLE");
        }
    }
}
