def get_piece(x, y, grid):
    piece = set()
    for i in range(y, len(grid)):
        for j in range(x, len(grid[i])):
            if grid[i][j] != '.':
                piece.add((j, i))
    return piece

def get_stable(grid):
    w, h = map(int, grid[0].split())
    grid = [list(row) for row in grid[1:]]

    pieces = {}
    for i in range(h):
        for j in range(w):
            if grid[i][j] != '.':
                piece = get_piece(j, i, grid)
                pieces[grid[i][j]] = piece

    for piece_id in pieces:
        piece = pieces[piece_id]
        x_coords = [x for x, y in piece]
        xL = min(x_coords)
        xR = max(x_coords)

        total_mass = 0
        for x, y in piece:
            total_mass += x

        M = total_mass // len(piece)
        if xL <= M <= xR:
            continue
        else:
            return "UNSTABLE"

    return "STABLE"

def main():
    while True:
        line = input()
        if line == "0 0":
            break
        grid = [line]
        while True:
            line = input()
            if line == "0 0":
                break
            grid.append(line)
        print(get_stable(grid))

if __name__ == "__main__":
    main()
