#include <iostream>
#include <queue>
#include <string>
#include <vector>
#include <algorithm>

using namespace std;

struct Node {
    int id;
    string label;
    vector<Node*> next;
};

void dfs(Node* node, string path, vector<Node*>& visited, vector<Node*>& stack) {
    visited[node->id] = true;
    stack.push_back(node);
    for (Node* next : node->next) {
        if (!visited[next->id]) {
            dfs(next, path + next->label, visited, stack);
        }
    }
}

int main() {
    int n, a, s, g;
    string line;
    getline(cin, line);
    while (getline(cin, line)) {
        if (line == "0 0 0 0") break;
        istringstream iss(line);
        iss >> n >> a >> s >> g;
        s--; g--;
        vector<Node*> nodes(n);
        for (int i = 0; i < n; i++) {
            nodes[i] = new Node();
            nodes[i]->id = i;
        }
        for (int i = 0; i < a; i++) {
            getline(cin, line);
            istringstream iss(line);
            int x, y;
            string label;
            iss >> x >> y >> label;
            x--; y--;
            nodes[x]->next.push_back(nodes[y]);
            nodes[y]->label += label;
        }
        vector<Node*> visited(n, nullptr);
        vector<Node*> stack;
        dfs(nodes[s], "", visited, stack);
        if (visited[g] == nullptr) {
            cout << "NO" << endl;
            continue;
        }
        string ans = "";
        for (Node* node : stack) {
            if (node->id == g) break;
            ans += node->label;
        }
        sort(ans.begin(), ans.end());
        bool found = true;
        for (int i = 0; i < ans.size(); i++) {
            for (int j = i + 1; j <= ans.size(); j++) {
                string substr = ans.substr(i, j - i);
                if (substr > ans.substr(0, i)) {
                    found = false;
                    break;
                }
            }
            if (!found) break;
        }
        if (found) {
            cout << ans << endl;
        } else {
            cout << "NO" << endl;
        }
        for (Node* node : stack) {
            delete node;
        }
    }
    return 0;
}
