#include <stdio.h>
#include <math.h>

#define EPS 0.001
#define MAX_MIRRORS 5

typedef struct {
    double x, y;
} Point;

typedef struct {
    Point p, q;
} Mirror;

typedef struct {
    Point l, t;
    Mirror mirrors[MAX_MIRRORS];
    int n;
} Problem;

double distance(Point p1, Point p2) {
    return sqrt(pow(p1.x - p2.x, 2) + pow(p1.y - p2.y, 2));
}

double angle(Point p1, Point p2, Point p3) {
    double v1x = p2.x - p1.x, v1y = p2.y - p1.y;
    double v2x = p3.x - p2.x, v2y = p3.y - p2.y;
    return atan2(v1x * v2y - v1y * v2x, v1x * v2x + v1y * v2y);
}

double reflect(Point p, Point m1, Point m2) {
    double a = angle(m1, p, m2);
    double b = angle(m1, m2, p);
    return distance(p, m1) * sin(2 * a) / sin(a + b);
}

double solve(Problem p) {
    double min_distance = 1e9;
    for (int i = 0; i < p.n; i++) {
        Point l = p.l, t = p.t;
        for (int j = 0; j < 6; j++) {
            double d = distance(l, t);
            if (d < min_distance && d > EPS) {
                min_distance = d;
            }
            if (i == p.n - 1) {
                break;
            }
            Point m1 = p.mirrors[i].p, m2 = p.mirrors[i].q;
            if (distance(l, m1) < EPS || distance(l, m2) < EPS) {
                continue;
            }
            if (distance(t, m1) < EPS || distance(t, m2) < EPS) {
                continue;
            }
            double a = angle(m1, l, m2);
            double b = angle(m1, m2, l);
            if (sin(a) < 0.1 || sin(b) < 0.1) {
                continue;
            }
            double r = reflect(l, m1, m2);
            if (r < EPS) {
                continue;
            }
            l = (Point) {l.x + r * cos(a), l.y + r * sin(a)};
        }
    }
    return min_distance;
}

int main() {
    Problem p;
    int n;
    while (scanf("%d", &n) == 1 && n != 0) {
        for (int i = 0; i < n; i++) {
            scanf("%lf %lf %lf %lf", &p.mirrors[i].p.x, &p.mirrors[i].p.y, &p.mirrors[i].q.x, &p.mirrors[i].q.y);
        }
        scanf("%lf %lf %lf %lf", &p.l.x, &p.l.y, &p.t.x, &p.t.y);
        p.n = n;
        printf("%.6f\n", solve(p));
    }
    return 0;
}
