import math

def distance(p1, p2):
    return math.sqrt((p1[0] - p2[0])**2 + (p1[1] - p2[1])**2)

def angle(p1, p2, p3):
    v1 = [p2[0] - p1[0], p2[1] - p1[1]]
    v2 = [p3[0] - p2[0], p3[1] - p2[1]]
    return math.acos((v1[0]*v2[0] + v1[1]*v2[1]) / (math.sqrt(v1[0]**2 + v1[1]**2) * math.sqrt(v2[0]**2 + v2[1]**2)))

def reflect(p, n):
    return [p[0] - 2*n[0]*math.cos(2*angle(p, [0, 0], n)), p[1] - 2*n[1]*math.cos(2*angle(p, [0, 0], n))]

def shortest_path(mirrors, target, source):
    queue = [(source, [source], 0)]
    visited = set()
    while queue:
        (x, y), path, dist = queue.pop(0)
        if (x, y) == target:
            return dist
        for mirror in mirrors:
            if (x, y) != mirror[0] and (x, y) != mirror[1]:
                n = [(mirror[1][0] - mirror[0][0]), (mirror[1][1] - mirror[0][1])]
                n = [n[0]/math.sqrt(n[0]**2 + n[1]**2), n[1]/math.sqrt(n[0]**2 + n[1]**2)]
                p = [x, y]
                if math.fabs(angle(p, mirror[0], n) - math.pi/2) > math.asin(0.1):
                    r = reflect(p, n)
                    if (r[0], r[1]) not in visited:
                        queue.append((r[0], r[1]), path + [(r[0], r[1])], dist + distance(p, r))
                        visited.add((r[0], r[1]))
        for mirror in mirrors:
            if (x, y) != mirror[0] and (x, y) != mirror[1]:
                n = [(mirror[1][0] - mirror[0][0]), (mirror[1][1] - mirror[0][1])]
                n = [n[0]/math.sqrt(n[0]**2 + n[1]**2), n[1]/math.sqrt(n[0]**2 + n[1]**2)]
                p = [x, y]
                if math.fabs(angle(p, mirror[0], n) - math.pi/2) > math.asin(0.1):
                    r = reflect(p, n)
                    if (r[0], r[1]) not in visited:
                        queue.append((r[0], r[1]), path + [(r[0], r[1])], dist + distance(p, r))
                        visited.add((r[0], r[1]))
    return -1

def main():
    while True:
        n = int(input())
        if n == 0:
            break
        mirrors = []
        for _ in range(n):
            x, y, x1, y1 = map(int, input().split())
            mirrors.append([(x, y), (x1, y1)])
        x, y = map(int, input().split())
        x1, y1 = map(int, input().split())
        print(shortest_path(mirrors, (x1, y1), (x, y)))

if __name__ == "__main__":
    main()
