#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_LENGTH 100

int is_balanced(char *str) {
    int stack_size = 0;
    int round_stack[100];
    int square_stack[100];
    int round_top = 0;
    int square_top = 0;

    for (int i = 0; i < strlen(str); i++) {
        if (str[i] == '(') {
            round_stack[round_top++] = i;
        } else if (str[i] == '[') {
            square_stack[square_top++] = i;
        } else if (str[i] == ')') {
            if (round_top == 0) return 0;
            int j = round_stack[--round_top];
            if (j > i) return 0;
            for (int k = j + 1; k < i; k++) {
                if (str[k] == '(' || str[k] == '[') continue;
                return 0;
            }
        } else if (str[i] == ']') {
            if (square_top == 0) return 0;
            int j = square_stack[--square_top];
            if (j > i) return 0;
            for (int k = j + 1; k < i; k++) {
                if (str[k] == '(' || str[k] == '[') continue;
                return 0;
            }
        }
    }

    if (round_top != 0 || square_top != 0) return 0;

    return 1;
}

int main() {
    char str[MAX_LENGTH];
    while (1) {
        fgets(str, MAX_LENGTH, stdin);
        str[strcspn(str, "\n")] = 0; // remove newline character
        if (strcmp(str, ".") == 0) break;
        if (is_balanced(str)) printf("yes\n");
        else printf("no\n");
    }
    return 0;
}
