#include <stdio.h>
#include <stdlib.h>

#define MAX_H 8
#define MAX_W 8
#define MAX_COLOR 6

int h, w, c;
int grid[MAX_H][MAX_W];

int dfs(int x, int y, int color, int shock) {
    if (x < 0 || x >= h || y < 0 || y >= w || grid[x][y] == 0) {
        return 0;
    }
    if (grid[x][y] == color && shock < 5) {
        grid[x][y] = 0;
        return 1 + dfs(x - 1, y, color, shock) + dfs(x + 1, y, color, shock) +
               dfs(x, y - 1, color, shock) + dfs(x, y + 1, color, shock);
    } else if (grid[x][y] == color && shock >= 5) {
        grid[x][y] = 0;
        return 1;
    } else {
        return 0;
    }
}

int main() {
    int i, j;
    int max_size = 0;

    while (scanf("%d %d %d", &h, &w, &c) == 3) {
        if (h == 0 && w == 0 && c == 0) {
            break;
        }
        for (i = 0; i < h; i++) {
            for (j = 0; j < w; j++) {
                scanf("%d", &grid[i][j]);
            }
        }
        int size = dfs(0, 0, c, 0);
        printf("%d\n", size);
    }

    return 0;
}
