from collections import deque

def dfs(x, y, color, visited, colors):
    if x < 0 or x >= h or y < 0 or y >= w:
        return 0
    if visited[x][y]:
        return 0
    if colors[x][y] == color:
        return 1 + dfs(x-1, y, color, visited, colors) + dfs(x+1, y, color, visited, colors) + dfs(x, y-1, color, visited, colors) + dfs(x, y+1, color, visited, colors)
    return 0

def bfs(x, y, color, colors):
    visited = [[False]*w for _ in range(h)]
    visited[x][y] = True
    queue = deque([(x, y, 1)])
    max_size = 1
    while queue:
        x, y, size = queue.popleft()
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if nx >= 0 and nx < h and ny >= 0 and ny < w and not visited[nx][ny] and colors[nx][ny] == color:
                visited[nx][ny] = True
                queue.append((nx, ny, size + 1))
                max_size = max(max_size, size + 1)
    return max_size

def solve():
    global h, w, c, colors
    h, w, c = map(int, input().split())
    colors = [list(map(int, input().split())) for _ in range(h)]
    for _ in range(5):
        max_size = 0
        for i in range(h):
            for j in range(w):
                if colors[i][j] != c:
                    max_size = max(max_size, bfs(i, j, c, colors))
        if max_size == 0:
            break
        for i in range(h):
            for j in range(w):
                if colors[i][j] != c:
                    colors[i][j] = c
        for i in range(h):
            for j in range(w):
                if colors[i][j] == c:
                    colors[i][j] = 0
        for i in range(h):
            for j in range(w):
                if colors[i][j] == 0:
                    colors[i][j] = c
    for i in range(h):
        for j in range(w):
            if colors[i][j] == c:
                print(dfs(i, j, c, [[False]*w for _ in range(h)], colors))
                return

for _ in range(int(input())):
    solve()
