import java.util.*;
import java.io.*;

public class Main {
    static class Disc {
        int x, y, r, c;
        List<Disc> neighbors = new ArrayList<>();

        public Disc(int x, int y, int r, int c) {
            this.x = x;
            this.y = y;
            this.r = r;
            this.c = c;
        }
    }

    static class UnionFind {
        int[] parent;
        int[] size;

        public UnionFind(int n) {
            parent = new int[n];
            size = new int[n];
            for (int i = 0; i < n; i++) {
                parent[i] = i;
                size[i] = 1;
            }
        }

        public int find(int x) {
            if (parent[x] != x) {
                parent[x] = find(parent[x]);
            }
            return parent[x];
        }

        public void union(int x, int y) {
            int rootX = find(x);
            int rootY = find(y);
            if (rootX != rootY) {
                if (size[rootX] > size[rootY]) {
                    parent[rootY] = rootX;
                    size[rootX] += size[rootY];
                } else {
                    parent[rootX] = rootY;
                    size[rootY] += size[rootX];
                }
            }
        }
    }

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        int T = Integer.parseInt(br.readLine());
        while (T-- > 0) {
            int n = Integer.parseInt(br.readLine());
            Disc[] discs = new Disc[n];
            for (int i = 0; i < n; i++) {
                String[] line = br.readLine().split(" ");
                discs[i] = new Disc(Integer.parseInt(line[0]), Integer.parseInt(line[1]), Integer.parseInt(line[2]), Integer.parseInt(line[3]));
            }
            for (int i = 0; i < n; i++) {
                for (int j = i + 1; j < n; j++) {
                    double dist = Math.sqrt(Math.pow(discs[i].x - discs[j].x, 2) + Math.pow(discs[i].y - discs[j].y, 2));
                    if (dist + discs[i].r + discs[j].r == Math.max(discs[i].r, discs[j].r)) {
                        discs[i].neighbors.add(discs[j]);
                        discs[j].neighbors.add(discs[i]);
                    }
                }
            }
            UnionFind uf = new UnionFind(n);
            for (int i = 0; i < n; i++) {
                for (Disc d : discs[i].neighbors) {
                    uf.union(i, Arrays.asList(discs).indexOf(d));
                }
            }
            int[] colorCount = new int[5];
            for (int i = 0; i < n; i++) {
                colorCount[discs[i].c]++;
            }
            int ans = 0;
            for (int i = 0; i < 5; i++) {
                if (colorCount[i] > 0) {
                    int count = 0;
                    for (int j = 0; j < n; j++) {
                        if (discs[j].c == i && uf.find(j) == j) {
                            count++;
                        }
                    }
                    ans += count / 2;
                }
            }
            System.out.println(ans);
        }
    }
}
