import math
from collections import defaultdict

def distance(x1, y1, x2, y2):
    return math.sqrt((x1 - x2) ** 2 + (y1 - y2) ** 2)

def can_remove(discs, i, j):
    return discs[i]['x'] == discs[j]['x'] and discs[i]['y'] == discs[j]['y']

def dfs(discs, visited, color, i):
    if visited[i]:
        return 0
    visited[i] = True
    max_removal = 1
    for j in range(len(discs)):
        if discs[i]['color'] == discs[j]['color'] and not can_remove(discs, i, j) and not visited[j]:
            max_removal = max(max_removal, 1 + dfs(discs, visited, color, j))
    return max_removal

def solve():
    n = int(input())
    discs = []
    for _ in range(n):
        x, y, r, c = map(int, input().split())
        discs.append({'x': x, 'y': y, 'r': r, 'color': c})
    visited = [False] * n
    max_removal = 0
    for i in range(n):
        if not visited[i]:
            max_removal = max(max_removal, dfs(discs, visited, discs[i]['color'], i))
    print(max_removal)

while True:
    n = int(input())
    if n == 0:
        break
    solve()
