#include <stdio.h>
#include <stdlib.h>

#define MAX_H 32
#define MAX_W 32
#define MAX_S 10000

int max(int a, int b) {
    return (a > b) ? a : b;
}

int min(int a, int b) {
    return (a < b) ? a : b;
}

int max_suppressed_demand(int h, int w, int s, int demand[MAX_H][MAX_W]) {
    int max_suppressed = 0;
    int i, j, k, l;

    for (i = 0; i < (1 << (h + w)); i++) {
        int suppressed = 0;
        int reserve = s;
        int groups = 1;

        for (j = 0; j < h; j++) {
            for (k = 0; k < w; k++) {
                if ((i >> (j + k)) & 1) {
                    suppressed += demand[j][k];
                    groups++;
                }
            }
        }

        if (suppressed <= s && suppressed > max_suppressed) {
            max_suppressed = suppressed;
            reserve = s - max_suppressed;
        }
    }

    return max_suppressed;
}

int main() {
    int h, w, s;
    int demand[MAX_H][MAX_W];
    int i, j;

    while (scanf("%d %d %d", &h, &w, &s) == 3) {
        if (h == 0 && w == 0 && s == 0) {
            break;
        }

        for (i = 0; i < h; i++) {
            for (j = 0; j < w; j++) {
                scanf("%d", &demand[i][j]);
            }
        }

        int max_suppressed = max_suppressed_demand(h, w, s, demand);
        int groups = 1 << (h + w);

        printf("%d %d\n", groups - 1, max(s - max_suppressed, 0));
    }

    return 0;
}
