import sys

def max_suppressed_demand(grid):
    h, w = len(grid), len(grid[0])
    dp = [[0] * w for _ in range(h)]
    for i in range(h):
        for j in range(w):
            if i == 0 or j == 0:
                dp[i][j] = grid[i][j]
            else:
                dp[i][j] = grid[i][j] + max(dp[i-1][j], dp[i][j-1])
    return dp[-1][-1]

def max_groups(grid):
    h, w = len(grid), len(grid[0])
    dp = [[0] * w for _ in range(h)]
    for i in range(h):
        for j in range(w):
            if i == 0 or j == 0:
                dp[i][j] = 1
            else:
                dp[i][j] = 1 + max(dp[i-1][j], dp[i][j-1])
    return dp[-1][-1]

def max_reserve_power(grid, s):
    h, w = len(grid), len(grid[0])
    max_suppressed = max_suppressed_demand(grid)
    max_groups_count = max_groups(grid)
    return max_groups_count, s - max_suppressed

def solve(h, w, s, grid):
    max_groups_count, max_reserve_power = max_reserve_power(grid, s)
    return f"{max_groups_count} {max_reserve_power}"

def main():
    input_lines = sys.stdin.readlines()
    i = 0
    while i < len(input_lines):
        h, w, s = map(int, input_lines[i].split())
        i += 1
        grid = []
        for _ in range(h):
            grid.append(list(map(int, input_lines[i].split())))
            i += 1
        print(solve(h, w, s, grid))
    return

if __name__ == "__main__":
    main()
