#include <stdio.h>
#include <math.h>

int main() {
    int len, x1, y1, x2, y2;
    double result;

    while (scanf("%d %d %d %d %d", &len, &x1, &y1, &x2, &y2) == 5) {
        if (len == 0 && x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0) {
            break;
        }

        double min_x = 0;
        double max_x = 0;
        double min_y = 0;
        double max_y = 0;

        if (x1 < 0) {
            min_x = -x1;
        } else {
            max_x = x2;
        }

        if (y1 < 0) {
            min_y = -y1;
        } else {
            max_y = y2;
        }

        double fence_length = 0;

        if (len >= max_x) {
            fence_length += 2 * (max_y - min_y);
        } else {
            fence_length += 2 * sqrt(pow(max_x - len, 2) + pow(max_y - min_y, 2));
        }

        if (len >= max_y) {
            fence_length += 2 * (max_x - min_x);
        } else {
            fence_length += 2 * sqrt(pow(max_y - len, 2) + pow(max_x - min_x, 2));
        }

        result = fence_length;
        printf("%.10f\n", result);
    }

    return 0;
}
