#include <iostream>
#include <cmath>

int main() {
    while (true) {
        int len, x1, y1, x2, y2;
        std::cin >> len >> x1 >> y1 >> x2 >> y2;

        if (x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0)
            break;

        double fence = 0;
        double dist = std::sqrt(std::pow(x1, 2) + std::pow(y1, 2));
        if (dist > len)
            fence += 2 * len;
        else
            fence += 2 * dist;

        dist = std::sqrt(std::pow(x2, 2) + std::pow(y2, 2));
        if (dist > len)
            fence += 2 * len;
        else
            fence += 2 * dist;

        dist = std::abs(x2 - x1);
        if (dist > len)
            fence += 2 * len;
        else
            fence += 2 * dist;

        dist = std::abs(y2 - y1);
        if (dist > len)
            fence += 2 * len;
        else
            fence += 2 * dist;

        double left = std::min(std::abs(x1), std::abs(x2));
        double right = std::max(std::abs(x1), std::abs(x2));
        double top = std::min(std::abs(y1), std::abs(y2));
        double bottom = std::max(std::abs(y1), std::abs(y2));

        if (left > len)
            fence += 2 * len;
        else
            fence += 2 * left;

        if (right > len)
            fence += 2 * len;
        else
            fence += 2 * right;

        if (top > len)
            fence += 2 * len;
        else
            fence += 2 * top;

        if (bottom > len)
            fence += 2 * len;
        else
            fence += 2 * bottom;

        std::cout << std::setprecision(15) << fence << std::endl;
    }

    return 0;
}
