import java.util.Scanner;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            int len = scanner.nextInt();
            int x1 = scanner.nextInt();
            int y1 = scanner.nextInt();
            int x2 = scanner.nextInt();
            int y2 = scanner.nextInt();
            if (len == 0 && x1 == 0 && y1 == 0 && x2 == 0 && y2 == 0) {
                break;
            }
            double result = calculateFenceLength(len, x1, y1, x2, y2);
            System.out.printf("%.10f%n", result);
        }
        scanner.close();
    }

    public static double calculateFenceLength(int len, int x1, int y1, int x2, int y2) {
        double result = 0;
        if (x1 < 0 && x2 > 0) {
            result += Math.min(Math.abs(x1), Math.abs(x2)) + len;
        } else if (x1 > 0 && x2 < 0) {
            result += Math.min(Math.abs(x1), Math.abs(x2)) + len;
        }
        if (y1 < 0 && y2 > 0) {
            result += Math.min(Math.abs(y1), Math.abs(y2)) + len;
        } else if (y1 > 0 && y2 < 0) {
            result += Math.min(Math.abs(y1), Math.abs(y2)) + len;
        }
        return result;
    }
}
