import java.io.BufferedReader;
import java.io.IOException;
import java.io.InputStreamReader;
import java.util.*;

public class Main {
    static class Pair {
        int x, y;

        public Pair(int x, int y) {
            this.x = x;
            this.y = y;
        }
    }

    static int[][] dirs = {{0, 1}, {0, -1}, {1, 0}, {-1, 0}};

    public static void main(String[] args) throws IOException {
        BufferedReader br = new BufferedReader(new InputStreamReader(System.in));
        String[] input;
        while ((input = br.readLine().split(" ")) != null && input.length > 0) {
            int h = Integer.parseInt(input[0]);
            int w = Integer.parseInt(input[1]);
            int[][] maze = new int[h][w];
            for (int i = 0; i < h; i++) {
                String line = br.readLine();
                if (i == 0) {
                    for (int j = 1; j < w; j++) {
                        maze[i][j] = Integer.parseInt(line.split(" ")[j - 1]);
                    }
                } else {
                    for (int j = 0; j < w; j++) {
                        maze[i][j] = Integer.parseInt(line.split(" ")[j]);
                    }
                }
            }
            int[][] dist = new int[h][w];
            for (int i = 0; i < h; i++) {
                Arrays.fill(dist[i], -1);
            }
            Queue<Pair> q = new LinkedList<>();
            q.add(new Pair(0, 0));
            dist[0][0] = 0;
            int minCards = 0;
            while (!q.isEmpty()) {
                Pair p = q.poll();
                int x = p.x;
                int y = p.y;
                for (int[] dir : dirs) {
                    int nx = x + dir[0];
                    int ny = y + dir[1];
                    if (nx >= 0 && nx < h && ny >= 0 && ny < w && maze[nx][ny] == 0) {
                        if (dist[nx][ny] == -1) {
                            dist[nx][ny] = dist[x][y] + 1;
                            q.add(new Pair(nx, ny));
                        }
                    }
                }
                if (x == h - 1 && y == w - 1) {
                    minCards = Math.max(minCards, dist[x][y]);
                }
            }
            if (minCards == 0) {
                System.out.println(-1);
            } else {
                System.out.println(minCards);
            }
        }
    }
}
