from collections import deque

def bfs(maze, start, end):
    queue = deque([(start, 0)])
    visited = set()
    while queue:
        (x, y), dist = queue.popleft()
        if (x, y) == end:
            return dist
        if (x, y) in visited:
            continue
        visited.add((x, y))
        for dx, dy in [(-1, 0), (1, 0), (0, -1), (0, 1)]:
            nx, ny = x + dx, y + dy
            if 0 <= nx < len(maze) and 0 <= ny < len(maze[0]) and maze[nx][ny] == 0:
                queue.append(((nx, ny), dist + 1))
    return -1

def solve(maze):
    h, w = len(maze), len(maze[0])
    start = (0, 0)
    end = (h - 1, w - 1)
    ans = -1
    for i in range(h):
        for j in range(w):
            if maze[i][j] == 0:
                temp = [row[:] for row in maze]
                temp[i][j] = 1
                ans = max(ans, bfs(temp, start, end))
    return ans

def main():
    while True:
        try:
            h, w = map(int, input().split())
            maze = []
            for _ in range(h):
                row = list(map(int, input().split()))
                maze.append(row)
            print(solve(maze))
        except ValueError:
            break

if __name__ == "__main__":
    main()
