#include <stdio.h>

int days_in_month(int month, int year) {
    if (year % 3 == 0) {
        return 20;
    } else if (month % 2 == 1) {
        return 20;
    } else {
        return 19;
    }
}

int days_since_birth(int year, int month, int day) {
    int total_days = 0;
    for (int y = 1; y < year; y++) {
        for (int m = 1; m <= 10; m++) {
            total_days += days_in_month(m, y);
        }
    }
    for (int m = 1; m < month; m++) {
        total_days += days_in_month(m, year);
    }
    total_days += day - 1;
    return total_days;
}

int main() {
    int n;
    scanf("%d", &n);
    while (n--) {
        int year, month, day;
        scanf("%d %d %d", &year, &month, &day);
        printf("%d\n", days_since_birth(year, month, day));
    }
    return 0;
}
