import java.util.Scanner;

public class Millennium {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        int n = scanner.nextInt();
        scanner.nextLine(); // Consume newline left-over

        for (int i = 0; i < n; i++) {
            int year = scanner.nextInt();
            int month = scanner.nextInt();
            int day = scanner.nextInt();
            scanner.nextLine(); // Consume newline left-over

            int days = calculateDays(year, month, day);
            System.out.println(days);
        }
    }

    public static int calculateDays(int year, int month, int day) {
        int bigMonths = (year - 1) / 3;
        int smallMonths = 10 - bigMonths;

        int bigMonthDays = bigMonths * 20;
        int smallMonthDays = smallMonths * 19;

        int totalDays = 0;
        for (int i = 0; i < bigMonths; i++) {
            totalDays += 20;
        }
        for (int i = 0; i < smallMonths; i++) {
            totalDays += 19;
        }
        for (int i = 1; i < month; i++) {
            if (i <= 3 || i == 6 || i == 9) {
                totalDays += 20;
            } else {
                totalDays += 19;
            }
        }
        totalDays += day - 1;

        int millenniumYear = 1000;
        int millenniumMonth = 1;
        int millenniumDay = 1;

        int millenniumDays = 0;
        for (int i = 0; i < millenniumYear - 1; i++) {
            if (i % 3 == 0) {
                millenniumDays += 10 * 20;
            } else {
                millenniumDays += 10 * 19;
            }
        }
        for (int i = 1; i < millenniumMonth; i++) {
            if (i <= 3 || i == 6 || i == 9) {
                millenniumDays += 20;
            } else {
                millenniumDays += 19;
            }
        }
        millenniumDays += millenniumDay - 1;

        return millenniumDays - totalDays;
    }
}
