#include <stdio.h>
#include <stdlib.h>
#include <string.h>

#define MAX_DIGITS 6
#define MAX_VALUE (int)pow(10, MAX_DIGITS)

int compare_integers(const void *a, const void *b) {
    return (*(int*)a - *(int*)b);
}

int rearrange_digits(int value, int digits) {
    char str[MAX_DIGITS + 1];
    sprintf(str, "%0*d", digits, value);
    int max_value = 0, min_value = MAX_VALUE;
    for (int i = 0; i < (1 << digits); i++) {
        int temp = 0;
        for (int j = 0; j < digits; j++) {
            if ((i >> j) & 1) {
                temp = temp * 10 + (str[j] - '0');
            }
        }
        max_value = (temp > max_value) ? temp : max_value;
        temp = 0;
        for (int j = 0; j < digits; j++) {
            if (!((i >> j) & 1)) {
                temp = temp * 10 + (str[j] - '0');
            }
        }
        min_value = (temp < min_value) ? temp : min_value;
    }
    return max_value - min_value;
}

int main() {
    int a0, digits;
    while (scanf("%d %d", &a0, &digits) == 2) {
        if (a0 == 0 && digits == 0) {
            break;
        }
        int sequence[MAX_VALUE];
        int sequence_size = 1;
        sequence[0] = a0;
        for (int i = 0; i < MAX_VALUE; i++) {
            sequence[i] = rearrange_digits(sequence[i % sequence_size], digits);
            if (i >= sequence_size) {
                sequence_size++;
            }
            for (int j = 0; j < i; j++) {
                if (sequence[i] == sequence[j]) {
                    printf("%d %d %d\n", j, sequence[i], i - j);
                    goto next_dataset;
                }
            }
        }
        next_dataset:;
    }
    return 0;
}
