#include <iostream>
#include <string>
#include <vector>
#include <algorithm>

std::string rearrange(const std::string& str, bool largest) {
    std::string largestStr = str;
    std::string smallestStr = str;
    std::sort(largestStr.begin(), largestStr.end(), std::greater<char>());
    std::sort(smallestStr.begin(), smallestStr.end());
    if (largest) {
        return largestStr;
    } else {
        return smallestStr;
    }
}

int main() {
    while (true) {
        int a0, L;
        std::cin >> a0 >> L;
        if (a0 == 0 && L == 0) {
            break;
        }
        std::string str = std::to_string(a0);
        while (str.length() < L) {
            str = "0" + str;
        }
        std::vector<std::string> sequence;
        sequence.push_back(str);
        for (int i = 0; i < 20; ++i) {
            str = rearrange(str, true);
            str = std::to_string(std::stoll(str) - std::stoll(rearrange(str, false)));
            while (str.length() < L) {
                str = "0" + str;
            }
            if (std::find(sequence.begin(), sequence.end(), str) != sequence.end()) {
                int j = std::distance(sequence.begin(), std::find(sequence.begin(), sequence.end(), str));
                std::cout << j << " " << str << " " << i - j + 1 << std::endl;
                break;
            }
            sequence.push_back(str);
        }
    }
    return 0;
}
