import java.util.*;

public class Main {
    public static void main(String[] args) {
        Scanner scanner = new Scanner(System.in);
        while (true) {
            String[] input = scanner.nextLine().split(" ");
            if (input[0].equals("0") && input[1].equals("0")) {
                break;
            }
            int a0 = Integer.parseInt(input[0]);
            int L = Integer.parseInt(input[1]);
            Set<String> seen = new HashSet<>();
            int j = 0;
            int i = 0;
            while (true) {
                String a = String.format("%0" + L + "d", a0);
                if (seen.contains(a)) {
                    break;
                }
                seen.add(a);
                i++;
                a0 = getLargest(a) - getSmallest(a);
                j++;
            }
            System.out.println(j + " " + getSmallest(a) + " " + (i - j));
        }
    }

    public static int getLargest(String a) {
        char[] chars = a.toCharArray();
        Arrays.sort(chars);
        return Integer.parseInt(new String(chars));
    }

    public static int getSmallest(String a) {
        char[] chars = a.toCharArray();
        Arrays.sort(chars);
        for (int i = 0; i < chars.length; i++) {
            if (chars[i] != '0') {
                break;
            }
        }
        return Integer.parseInt(new String(chars, i, chars.length - i));
    }
}
