#include <stdio.h>
#include <stdlib.h>

typedef struct {
    int top;
    int front;
} Die;

void roll(Die *die) {
    int max = 0;
    int max_index = 0;
    for (int i = 4; i <= 6; i++) {
        if (i != die->top && i != die->front && i > max) {
            max = i;
            max_index = i;
        }
    }
    die->top = max_index;
}

void fall(Die *die, int *counts) {
    if (die->top == 4) {
        if (die->front == 1) {
            die->front = 6;
        } else if (die->front == 2) {
            die->front = 5;
        } else if (die->front == 3) {
            die->front = 4;
        }
    } else if (die->top == 5) {
        if (die->front == 1) {
            die->front = 3;
        } else if (die->front == 2) {
            die->front = 6;
        } else if (die->front == 3) {
            die->front = 2;
        }
    } else if (die->top == 6) {
        if (die->front == 1) {
            die->front = 2;
        } else if (die->front == 2) {
            die->front = 1;
        } else if (die->front == 3) {
            die->front = 6;
        } else if (die->front == 4) {
            die->front = 5;
        } else if (die->front == 5) {
            die->front = 4;
        }
    }
    counts[die->top - 1]++;
    counts[die->front - 1]++;
}

int main() {
    int n;
    while (scanf("%d", &n) == 1) {
        if (n == 0) break;
        Die dice[n];
        int counts[6] = {0};
        for (int i = 0; i < n; i++) {
            scanf("%d %d", &dice[i].top, &dice[i].front);
        }
        for (int i = 0; i < n; i++) {
            fall(&dice[i], counts);
            roll(&dice[i]);
        }
        for (int i = 0; i < 6; i++) {
            printf("%d ", counts[i]);
        }
        printf("\n");
    }
    return 0;
}
